import React, {useState} from 'react';

function RecipeSearchBar() {


    const [newRecipeSearch, setNewRecipeSearch] = useState("")

    function handleRecipieChange(e) {
        setNewRecipeSearch(e.target.value)
    }

    //search for recipies
    function handleRecipeSubmit (e) {
        e.preventDefault()
        fetch("/search_recipes", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                "Accepts": 'application/json'
            },
            body: JSON.stringify({"query": newRecipeSearch})
        })
        .then(response => response.json())
        .then(filteredRecipeObject => setSearchedRecipes(filteredRecipeObject["recipes"]))
        }

    return (

        <form  className="recipe-search-container" nSubmit={handleRecipeSubmit} >
            <div>
                <div  className="recipe-search-title">
                    <h3 className="recipe-search-title-header"></h3>
                    <input
                    className="recipe-search-bar fs-6"
                    type="text"
                    placeholder="search recepies"
                    onChange={handleRecipieChange}
                    value={newRecipeSearch}
                    />
                </div>
            </div>
        </form> 
    )
}

export default RecipeSearchBar;
