import React from "react";
import {Link} from 'react-router-dom';
import UserDetails from './UserDetails';
import login_button_image from "./assets/person-circle.svg";
function NavBar ({ currentUser, logout}) {

    return (

        <nav class="navbar navbar-expand-lg bg-body-tertiary">
            <div ss="container-fluid">
            <Link to="/welcome" className="logo">the logo</Link>
            <div>
                { currentUser?.username?.length > 0
                ? (
                    <div>
                        <UserDetails currentUser={currentUser} logout={logout}/>
                    </div>
                )
                : (
                    <div>
                    <img login={login_button_image}/>

                    <Link to='/login'><button class="bi bi-person-circle" >Login</button></Link>

                    </div>
                )}
            </div>



            </div>
        </nav>
    )
}

export default NavBar;
