import React from 'react';

function LoginPage({attemptLogin, currentUser}) {

    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")

    const handleChangeUsername = e => setUsername(e.target.value)
    const handleChangePassword = e => setPassword(e.target.value)

    const [isLoggedIn, setIsLoggedIn] = useState(false);

    function handleSubmit(e) {
        e.preventDefault();
        attemptLogin({"username": username, "password": password })
    }

    function createLoginAlert(e) {
        if (isLoggedIn) {
            alert("Logged in successfully!")
        } else {
            alert("Please try again ")
        }
    }


    return (
        <div>
            <form className= "login-page=container">
                <div className ="username-group">
                        <h2>Username</h2>
                        <input
                            className="username-input"
                            type="text"
                            onChange={handleChangeUsername}
                            placeholder="enter username!"
                            value={username}
                        />
                    </div>
                    <div className ="password-group">
                        <h2>Password</h2>
                        <input
                            className="password-input"
                            type="text"
                            onChange={handleChangePassword}
                            placeholder="enter a password!"
                            value={password}
                        />
                    </div>
                    <button className="submit-button" type="submit"> create a new account</button>
            </form>
        </div>
    )
}

export default LoginPage()
