import React from 'react';
import {Link, useNavigate} from 'react-router-dom';

function FoodItem ({item, addToFoodList, currentFoodResponse}) {
    const navigate = useNavigate()

    function handleClick(e) {
        addToFoodList(item)
        console.log("the current food response is", currentFoodResponse)
        if (currentFoodResponse)
        navigate('/food_log')


    }

    return (
        <div className="food-item" onClick={(e) => handleClick(e)} >
        <h1>{item.name}</h1>
        <p>{item.description}</p>
        <p>Calories: {item.calories} grams</p>
        </div> 
    )
}

