import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom';

function RecipeSearchBar({setSearchedRecipes}) {

    const navigate = useNavigate();
    const [newRecipeSearch, setNewRecipeSearch] = useState("")

    function handleRecipieChange(e) {
        setNewRecipeSearch(e.target.value)
    }
console.log("what am i sending as the query",newRecipeSearch)

//search for recipies
    function handleRecipeSubmit (e) {
        e.preventDefault()
        console.log("am i getting here")
        fetch('/search_recipes?query=${newRecipeSearch}', {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                "Accepts": 'application/json'
            },
            body: JSON.stringify({newRecipeSearch})
        })
        .then(response => response.json())
        .then(filteredRecipeObject => {
            console.log("what is the response i am getting back",filteredRecipeObject)
            setSearchedRecipes(filteredRecipeObject["recipes"])})
            navigate('/recipe_results')
        }

    return (

        <form  className="recipe-search-container" onSubmit={handleRecipeSubmit} >
            <div>
                <div  className="recipe-search-title">
                    <h3 className="recipe-search-title-header"></h3>
                    <input
                    className="recipe-search-bar fs-6"
                    type="text"
                    placeholder="search recepies"
                    onChange={handleRecipieChange}
                    value={newRecipeSearch}
                    />
                </div>
            </div>
        </form> 
    )
}

export default RecipeSearchBar;
