import './App.css';
import React, { useState } from "react";
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import TdeeCalculator from './Components/TdeeCalculator';
import FoodDashBoardPage from './Components/FoodDashboardPage'
import Error from './Error';
import SearchResultsPage from './Components/SearchResultsPage'

function App() {
  const [foodItems, setFoodItems] = useState([]) //the food items that are returned from the search 
  const [searchInput, setSearchInput] = useState("")
  const [selectedFoodItems, setSelectedFoodItems] = useState([]) //the food item that is selected to be added to the list 
  const [selectedMeal, setSelectedMeal] = useState("")

  function changeSearch(e) {
    setSearchInput(e)
  }

  function addToFoodList (foodToAdd) {  
      fetch('/add_to_food_list', {
          method: 'POST',
          headers: {
              "Content-Type": "application/JSON",
          },
          body: JSON.stringify({"item_id":foodToAdd},"meal_type" )
      })
  }

  return (
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<FoodDashBoardPage foodItems={foodItems} setFoodItems={setFoodItems}/>} />
          <Route path="search_food" element={<SearchResultsPage setFoodItems={setFoodItems} foodItems={foodItems} searchInput={searchInput} setSearchInput={setSearchInput} changeSearch={changeSearch} />} /> 
          <Route path="tdee_calculator" element={<TdeeCalculator />} />
          <Route path="*" element={<Error/>}/>
        </Routes>
      </BrowserRouter>

  );
}

export default App;
