import './App.css';
import React, { useState } from "react";
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import TdeeCalculator from './Components/TdeeCalculator';
import FoodDashBoardPage from './Components/FoodDashboardPage'
import Error from './Error';
import SearchResultsPage from './Components/SearchResultsPage'

function App() {
  const [foodItems, setFoodItems] = useState([]) //the food items that are returned from the search 
  const [selectedFoodItems, setSelectedFoodItems] = useState([]) //the food item that is selected to be added to the list 
  const [selectedMeal, setSelectedMeal] = useState("")

  function addToFoodList (foodToAdd) {   // add the selected food list to its designated area
      fetch('/add_to_food_list', {
          method: 'POST',
          headers: {
              "Content-Type": "application/JSON",
          },
          body: JSON.stringify({"item_id":foodToAdd}),
      })
      .then(response => response.json())
      .then(data => {
        setSelectedFoodItems(data)});

        // function addToFoodList (foodToAdd) {   // add the selected food list to its designated area
        //   fetch('/add_to_food_list', {
        //       method: 'POST',
        //       headers: {
        //           "Content-Type": "application/JSON",
        //       },
        //       body: JSON.stringify({"item_id":foodToAdd,"meal_type": selectedMeal}),
        //   })
        //   .then(response => response.json())
        //   .then(data => {
        //     setSelectedFoodItems(data)});


  return (
      <BrowserRouter>
        <Routes>
          <Route path="/" element={
                <FoodDashBoardPage 
                foodItems={foodItems} 
                setFoodItems={setFoodItems}
                setSelectedMeal={setSelectedMeal}
                />} />
          <Route path="search_food" element={
                <SearchResultsPage 
                setFoodItems={setFoodItems} foodItems={foodItems} 
                addToFoodList={addToFoodList}
                />} /> 
          <Route path="tdee_calculator" element={<TdeeCalculator />} />
          <Route path="*" element={<Error/>}/>
        </Routes>
      </BrowserRouter>

  );
}
}


export default App;
